using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI; using UnityEngine.SceneManagement;  public class LevelMenu : MonoBehaviour {     public Button[] buttons;     public GameObject[] pageButtons;     int unlockedLevel = 1;     int currentPage = 0;     int levelsPerPage = 6;       private void Awake()     {                ButtonsToArray();         if (PlayerPrefs.GetInt(AnimationStrings.UnlockedLevel) > unlockedLevel)         {             unlockedLevel = PlayerPrefs.GetInt(AnimationStrings.UnlockedLevel);                      }         UpdateButtons();     }      public void OpenLevel(int levelID)     {         string levelName = "Level" + levelID;         Time.timeScale = 1.0f;         SceneManager.LoadSceneAsync(levelName);     }      public void NextPage()     {         currentPage++;         UpdateButtons();     }      public void PreviousPage()     {         currentPage--;         UpdateButtons();     }      void ButtonsToArray()     {              }      void UpdateButtons()     {              }          public void MuteHandler(bool mute) {         if (mute) {             AudioListener.volume = 0f;         } else {             AudioListener.volume = 1f;         }     }  } 